import React, { useState, useRef } from 'react';
import { Send, Users, Edit3, Wand2, Mail, CheckCircle, AlertCircle, Loader2 } from 'lucide-react';

const AIEmailSender = () => {
  const [recipients, setRecipients] = useState(['']);
  const [prompt, setPrompt] = useState('');
  const [generatedEmail, setGeneratedEmail] = useState('');
  const [subject, setSubject] = useState('');
  const [isGenerating, setIsGenerating] = useState(false);
  const [isSending, setIsSending] = useState(false);
  const [apiKey, setApiKey] = useState('');
  const [showApiKey, setShowApiKey] = useState(false);
  const [status, setStatus] = useState({ type: '', message: '' });
  const [sentEmails, setSentEmails] = useState([]);
  
  const emailContentRef = useRef(null);

  // Add/Remove recipient functions
  const addRecipient = () => {
    setRecipients([...recipients, '']);
  };

  const removeRecipient = (index) => {
    if (recipients.length > 1) {
      setRecipients(recipients.filter((_, i) => i !== index));
    }
  };

  const updateRecipient = (index, value) => {
    const newRecipients = [...recipients];
    newRecipients[index] = value;
    setRecipients(newRecipients);
  };

  // Validate email format
  const isValidEmail = (email) => {
    return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
  };

  // Generate email using Groq API
  const generateEmail = async () => {
    if (!prompt.trim()) {
      setStatus({ type: 'error', message: 'Please enter a prompt for email generation.' });
      return;
    }

    if (!apiKey.trim()) {
      setStatus({ type: 'error', message: 'Please enter your Groq API key.' });
      return;
    }

    setIsGenerating(true);
    setStatus({ type: '', message: '' });

    try {
      const response = await fetch('https://api.groq.com/openai/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${apiKey}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          messages: [
            {
              role: 'system',
              content: 'You are an expert email writer. Generate professional, well-structured emails based on the user\'s prompt. Return only the email content without any additional formatting or explanations. Include a clear subject line at the beginning marked with "Subject:" followed by the email body.'
            },
            {
              role: 'user',
              content: `Generate an email based on this prompt: ${prompt}`
            }
          ],
          model: 'llama3-8b-8192',
          temperature: 0.7,
          max_tokens: 1000,
        }),
      });

      if (!response.ok) {
        throw new Error(`API Error: ${response.status}`);
      }

      const data = await response.json();
      const emailContent = data.choices[0].message.content;
      
      // Extract subject and body
      const lines = emailContent.split('\n');
      let extractedSubject = '';
      let emailBody = '';
      
      if (lines[0].toLowerCase().startsWith('subject:')) {
        extractedSubject = lines[0].replace(/^subject:\s*/i, '');
        emailBody = lines.slice(1).join('\n').trim();
      } else {
        emailBody = emailContent;
        extractedSubject = 'Generated Email';
      }
      
      setSubject(extractedSubject);
      setGeneratedEmail(emailBody);
      setStatus({ type: 'success', message: 'Email generated successfully!' });
      
    } catch (error) {
      console.error('Error generating email:', error);
      setStatus({ 
        type: 'error', 
        message: 'Failed to generate email. Please check your API key and try again.' 
      });
    } finally {
      setIsGenerating(false);
    }
  };

  // Simulate sending email (in a real app, this would connect to an email service)
  const sendEmail = async () => {
    const validRecipients = recipients.filter(email => email.trim() && isValidEmail(email.trim()));
    
    if (validRecipients.length === 0) {
      setStatus({ type: 'error', message: 'Please enter at least one valid email address.' });
      return;
    }

    if (!subject.trim() || !generatedEmail.trim()) {
      setStatus({ type: 'error', message: 'Please generate an email before sending.' });
      return;
    }

    setIsSending(true);
    setStatus({ type: '', message: '' });

    try {
      // Simulate API call delay
      await new Promise(resolve => setTimeout(resolve, 2000));
      
      const emailData = {
        subject: subject,
        body: generatedEmail,
        recipients: validRecipients,
        timestamp: new Date().toLocaleString(),
        id: Date.now()
      };
      
      setSentEmails(prev => [emailData, ...prev]);
      setStatus({ 
        type: 'success', 
        message: `Email sent successfully to ${validRecipients.length} recipient(s)!` 
      });
      
      // Reset form
      setRecipients(['']);
      setPrompt('');
      setGeneratedEmail('');
      setSubject('');
      
    } catch (error) {
      setStatus({ type: 'error', message: 'Failed to send email. Please try again.' });
    } finally {
      setIsSending(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-800 mb-2 flex items-center justify-center gap-3">
            <Mail className="text-blue-600" />
            AI Email Sender
          </h1>
          <p className="text-gray-600">Generate and send personalized emails using AI</p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Main Form */}
          <div className="lg:col-span-2 space-y-6">
            {/* API Key Section */}
            <div className="bg-white rounded-xl shadow-lg p-6">
              <h2 className="text-xl font-semibold text-gray-800 mb-4 flex items-center gap-2">
                <Wand2 className="text-purple-600" />
                Groq API Configuration
              </h2>
              <div className="space-y-3">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Groq API Key
                  </label>
                  <input
                    type={showApiKey ? "text" : "password"}
                    value={apiKey}
                    onChange={(e) => setApiKey(e.target.value)}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="Enter your Groq API key"
                  />
                  <button
                    type="button"
                    onClick={() => setShowApiKey(!showApiKey)}
                    className="mt-1 text-sm text-blue-600 hover:text-blue-800"
                  >
                    {showApiKey ? 'Hide' : 'Show'} API Key
                  </button>
                </div>
                <p className="text-xs text-gray-500">
                  Get your free API key from <a href="https://console.groq.com/" target="_blank" rel="noopener noreferrer" className="text-blue-600 hover:underline">console.groq.com</a>
                </p>
              </div>
            </div>

            {/* Recipients Section */}
            <div className="bg-white rounded-xl shadow-lg p-6">
              <h2 className="text-xl font-semibold text-gray-800 mb-4 flex items-center gap-2">
                <Users className="text-green-600" />
                Recipients
              </h2>
              <div className="space-y-3">
                {recipients.map((recipient, index) => (
                  <div key={index} className="flex gap-2">
                    <input
                      type="email"
                      value={recipient}
                      onChange={(e) => updateRecipient(index, e.target.value)}
                      className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      placeholder="recipient@example.com"
                    />
                    {recipients.length > 1 && (
                      <button
                        onClick={() => removeRecipient(index)}
                        className="px-3 py-2 text-red-600 hover:bg-red-50 rounded-lg"
                      >
                        ×
                      </button>
                    )}
                  </div>
                ))}
                <button
                  onClick={addRecipient}
                  className="text-blue-600 hover:text-blue-800 text-sm font-medium"
                >
                  + Add another recipient
                </button>
              </div>
            </div>

            {/* Email Generation Section */}
            <div className="bg-white rounded-xl shadow-lg p-6">
              <h2 className="text-xl font-semibold text-gray-800 mb-4 flex items-center gap-2">
                <Edit3 className="text-blue-600" />
                Email Generation
              </h2>
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Email Prompt
                  </label>
                  <textarea
                    value={prompt}
                    onChange={(e) => setPrompt(e.target.value)}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    rows="4"
                    placeholder="Describe the email you want to generate (e.g., 'Write a professional follow-up email for a job interview thanking the interviewer and expressing continued interest')"
                  />
                </div>
                <button
                  onClick={generateEmail}
                  disabled={isGenerating || !prompt.trim() || !apiKey.trim()}
                  className="w-full bg-gradient-to-r from-blue-600 to-purple-600 text-white py-3 px-6 rounded-lg font-medium hover:from-blue-700 hover:to-purple-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2"
                >
                  {isGenerating ? (
                    <>
                      <Loader2 className="animate-spin" size={20} />
                      Generating Email...
                    </>
                  ) : (
                    <>
                      <Wand2 size={20} />
                      Generate Email
                    </>
                  )}
                </button>
              </div>
            </div>

            {/* Generated Email Section */}
            {generatedEmail && (
              <div className="bg-white rounded-xl shadow-lg p-6">
                <h2 className="text-xl font-semibold text-gray-800 mb-4 flex items-center gap-2">
                  <Mail className="text-indigo-600" />
                  Generated Email (Editable)
                </h2>
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Subject
                    </label>
                    <input
                      type="text"
                      value={subject}
                      onChange={(e) => setSubject(e.target.value)}
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      placeholder="Email subject"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Email Content
                    </label>
                    <textarea
                      ref={emailContentRef}
                      value={generatedEmail}
                      onChange={(e) => setGeneratedEmail(e.target.value)}
                      className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      rows="12"
                      placeholder="Generated email content will appear here..."
                    />
                  </div>
                  <button
                    onClick={sendEmail}
                    disabled={isSending || !generatedEmail.trim() || recipients.every(r => !r.trim())}
                    className="w-full bg-gradient-to-r from-green-600 to-emerald-600 text-white py-3 px-6 rounded-lg font-medium hover:from-green-700 hover:to-emerald-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2"
                  >
                    {isSending ? (
                      <>
                        <Loader2 className="animate-spin" size={20} />
                        Sending Email...
                      </>
                    ) : (
                      <>
                        <Send size={20} />
                        Send Email
                      </>
                    )}
                  </button>
                </div>
              </div>
            )}
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            {/* Status Messages */}
            {status.message && (
              <div className={`rounded-xl p-4 flex items-center gap-3 ${
                status.type === 'success' 
                  ? 'bg-green-50 text-green-800 border border-green-200' 
                  : 'bg-red-50 text-red-800 border border-red-200'
              }`}>
                {status.type === 'success' ? (
                  <CheckCircle size={20} />
                ) : (
                  <AlertCircle size={20} />
                )}
                <span className="text-sm font-medium">{status.message}</span>
              </div>
            )}

            {/* Sent Emails History */}
            <div className="bg-white rounded-xl shadow-lg p-6">
              <h3 className="text-lg font-semibold text-gray-800 mb-4">Recent Emails</h3>
              {sentEmails.length === 0 ? (
                <p className="text-gray-500 text-sm">No emails sent yet.</p>
              ) : (
                <div className="space-y-3 max-h-96 overflow-y-auto">
                  {sentEmails.map((email) => (
                    <div key={email.id} className="border border-gray-200 rounded-lg p-3">
                      <div className="font-medium text-sm text-gray-800 mb-1">
                        {email.subject}
                      </div>
                      <div className="text-xs text-gray-500 mb-2">
                        To: {email.recipients.join(', ')}
                      </div>
                      <div className="text-xs text-gray-400">
                        {email.timestamp}
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* Instructions */}
            <div className="bg-blue-50 rounded-xl p-6 border border-blue-200">
              <h3 className="text-lg font-semibold text-blue-800 mb-3">How to Use</h3>
              <ol className="text-sm text-blue-700 space-y-2">
                <li>1. Enter your Groq API key</li>
                <li>2. Add recipient email addresses</li>
                <li>3. Write a prompt describing your email</li>
                <li>4. Click "Generate Email" to create content</li>
                <li>5. Edit the generated email as needed</li>
                <li>6. Click "Send Email" to deliver</li>
              </ol>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AIEmailSender;
